//some ahh sprite pointer

const uint8_t *get_piece_sprite(uint8_t piece)
{
    switch (piece) {
        case PIECE_W_PAWN:   return WSoldier;
        case PIECE_W_KNIGHT: return WHorse;
        case PIECE_W_BISHOP: return WBishop;
        case PIECE_W_ROOK:   return WCastle;
        case PIECE_W_QUEEN:  return WQueen;
        case PIECE_W_KING:   return WKing;

        case PIECE_B_PAWN:   return BSoldier;
        case PIECE_B_KNIGHT: return BHorse;
        case PIECE_B_BISHOP: return BBishop;
        case PIECE_B_ROOK:   return BCastle;
        case PIECE_B_QUEEN:  return BQueen;
        case PIECE_B_KING:   return BKing;

        default:             return NULL;
    }
}